<template>  <div :class="classes.wrapperClass">    <span v-if="showPrevNext" :class="classes.itemClass">      <button          :disabled="hasPrev"          :class="[classes.btnClass, classes.prevNextClass]"          @click="updatePage(prevPage)"      >        <slot name="prevPage"> Prev </slot>      </button>    </span>    <template v-if="hasFirst">      <span :class="classes.itemClass">        <button            :class="[              firstClass,              classes.btnClass            ]"            @click="updatePage(delta)"        >          {{ delta }}        </button>      </span>      <span        v-if="hasFirstBreakView"        :class="[classes.itemClass, classes.breakViewClass]"      >        <slot name="breakView"> ... </slot>      </span>    </template>    <span v-for="page in pages" :key="page" :class="classes.itemClass">      <button          :class="[            setActiveClass(page),            classes.btnClass          ]"          @click="updatePage(page)"      >        {{ page }}      </button>    </span>    <template v-if="hasLast">      <span        v-if="hasLastBreakView"        :class="[classes.itemClass, classes.breakViewClass]"      >        <slot name="breakView"> ... </slot>      </span>      <span :class="classes.itemClass">        <button            :class="[              lastClass,              classes.btnClass            ]"            @click="updatePage(totalPages)"        >          {{ totalPages }}        </button>      </span>    </template>    <span v-if="showPrevNext" :class="classes.itemClass">      <button          :disabled="hasNext"          :class="[classes.btnClass, classes.prevNextClass]"          @click="updatePage(nextPage)"      >        <slot name="nextPage"> Next </slot>      </button>    </span>  </div></template><script>const DELTA = 1const SECOND_ELEMENT = 2const EMIT_NAME = 'update-page'export default {  name: 'Pagination',  props: {    current: {      type: Number,      required: true,    },    perPage: {      type: Number,      required: true,    },    total: {      type: Number,      required: true,    },    pageRange: {      type: Number,      default: 2,    },    showPrevNext: {      type: Boolean,      default: true,    },    classes: {      type: Object,      default: () => ({        wrapperClass: 'v-simple-pagination',        itemClass: '',        btnClass: '',        breakViewClass: '',        prevNextClass: '',      }),    },    activeClass: {      type: String,      default: 'active',    },  },  data: () => ({    delta: DELTA,    secondElement: SECOND_ELEMENT  }),  computed: {    /**     * @returns {number}     */    rangeStart() {      const start = this.current - this.pageRange      return start >= this.delta ? start : this.delta    },    /**     * @returns {number}     */    rangeEnd() {      const end = this.current + this.pageRange      return end < this.totalPages ? end : this.totalPages    },    /**     * @returns {number[]}     */    pages() {      const pages = []      for (let i = this.rangeStart; i <= this.rangeEnd; i++) {        pages.push(i)      }      return pages    },    /**     * @returns {number}     */    nextPage() {      return this.current + this.delta    },    /**     * @returns {number}     */    prevPage() {      return this.current - this.delta    },    /**     * @returns {number}     */    totalPages() {      return Math.ceil(this.total / this.perPage) || this.delta    },    /**     * @returns {boolean}     */    hasFirst() {      return this.rangeStart !== this.delta    },    /**     * @returns {boolean}     */    hasLast() {      return this.rangeEnd < this.totalPages    },    /**     * @returns {boolean}     */    hasPrev() {      return this.current <= this.delta    },    /**     * @returns {boolean}     */    hasNext() {      return this.current >= this.totalPages    },    /**     * @returns {boolean}     */    hasFirstBreakView() {      return !this.pages.includes(this.secondElement)    },    /**     * @returns {boolean}     */    hasLastBreakView() {      return !this.pages.includes(this.totalPages - this.delta)    },    /**     * @returns {string}     */    firstClass() {      return this.current === this.delta ? this.activeClass : ''    },    /**     * @returns {string}     */    lastClass() {      return this.current === this.totalPages ? this.activeClass : ''    },  },  methods: {    /**     * @param {number} page     */    updatePage(page) {      this.$emit(EMIT_NAME, page)    },    /**     * @param {number} page     * @returns {string}     */    setActiveClass(page) {      return this.current === page ? this.activeClass : ''    },  },}</script>